import React, { useState, useEffect } from "react";
import axios from "axios";

const TransactionList = () => {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchTransactions();
  }, []);

  const fetchTransactions = async () => {
    try {
      const response = await axios.get(
        "http://localhost:5000/api/transactions"
      );
      setTransactions(response.data);
      setLoading(false);
    } catch (error) {
      setError("Failed to fetch transactions");
      setLoading(false);
      console.error("Error fetching transactions:", error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/transactions/${id}`);
      fetchTransactions();
    } catch (error) {
      setError("Failed to delete transaction");
      console.error("Error deleting transaction:", error);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
    });
  };

  const incomeTransactions = transactions.filter((t) => t.type === "income");
  const expenseTransactions = transactions.filter((t) => t.type === "expense");

  if (loading) return <div className="loading">Loading transactions...</div>;
  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="transaction-list">
      <div className="income-section">
        <h2>Income</h2>
        {incomeTransactions.length === 0 ? (
          <p className="no-transactions">No income transactions yet</p>
        ) : (
          incomeTransactions.map((transaction) => (
            <div key={transaction._id} className="transaction-item income">
              <div className="transaction-details">
                <span className="transaction-amount">
                  +₹{transaction.amount}
                </span>
                <span className="transaction-date">
                  {formatDate(transaction.date)}
                </span>
              </div>
              <button
                className="delete-btn"
                onClick={() => handleDelete(transaction._id)}
              >
                Delete
              </button>
            </div>
          ))
        )}
      </div>

      <div className="expense-section">
        <h2>Expenses</h2>
        {expenseTransactions.length === 0 ? (
          <p className="no-transactions">No expense transactions yet</p>
        ) : (
          expenseTransactions.map((transaction) => (
            <div key={transaction._id} className="transaction-item expense">
              <div className="transaction-details">
                <span className="transaction-category">
                  {transaction.category}
                </span>
                <span className="transaction-amount">
                  -₹{transaction.amount}
                </span>
                {transaction.description && (
                  <span className="transaction-description">
                    {transaction.description}
                  </span>
                )}
                <span className="transaction-date">
                  {formatDate(transaction.date)}
                </span>
              </div>
              <button
                className="delete-btn"
                onClick={() => handleDelete(transaction._id)}
              >
                Delete
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default TransactionList;
