import React, { useState } from "react";
import axios from "axios";

const TransactionForm = ({ onTransactionAdded }) => {
  const [formData, setFormData] = useState({
    type: "expense",
    amount: "",
    category: "",
    description: "",
  });

  const [error, setError] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
      // Reset category when switching to income
      ...(name === "type" && value === "income" ? { category: "Income" } : {}),
    }));
    setError("");
  };

  const validateForm = () => {
    if (
      !formData.amount ||
      isNaN(formData.amount) ||
      parseFloat(formData.amount) <= 0
    ) {
      setError("Please enter a valid amount");
      return false;
    }

    if (formData.type === "expense" && !formData.category) {
      setError("Please select a category for expenses");
      return false;
    }

    if (!formData.description.trim()) {
      setError("Please enter a description");
      return false;
    }

    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    if (!validateForm()) {
      return;
    }

    try {
      const transactionData = {
        ...formData,
        amount: parseFloat(formData.amount),
        category: formData.type === "income" ? "Income" : formData.category,
      };

      const response = await axios.post(
        "http://localhost:5000/api/transactions",
        transactionData
      );

      onTransactionAdded(response.data);

      // Reset form
      setFormData({
        type: "expense",
        amount: "",
        category: "",
        description: "",
      });
    } catch (err) {
      console.error("Error adding transaction:", err);
      setError(
        err.response?.data?.message ||
          "Failed to add transaction. Please try again."
      );
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      {error && (
        <div
          className="bg-error/10 border border-error text-error px-4 py-3 rounded relative"
          role="alert"
        >
          <span className="block sm:inline">{error}</span>
        </div>
      )}

      <div>
        <label className="block text-sm font-medium text-text-secondary mb-1">
          Type
        </label>
        <select
          name="type"
          value={formData.type}
          onChange={handleChange}
          className="select"
        >
          <option value="expense">Expense</option>
          <option value="income">Income</option>
        </select>
      </div>

      <div>
        <label className="block text-sm font-medium text-text-secondary mb-1">
          Amount
        </label>
        <input
          type="number"
          name="amount"
          value={formData.amount}
          onChange={handleChange}
          placeholder="Enter amount"
          step="0.01"
          min="0"
          className="input"
        />
      </div>

      {formData.type === "expense" && (
        <div>
          <label className="block text-sm font-medium text-text-secondary mb-1">
            Category
          </label>
          <select
            name="category"
            value={formData.category}
            onChange={handleChange}
            className="select"
          >
            <option value="">Select Category</option>
            <option value="Food">Food</option>
            <option value="Drinks">Drinks</option>
            <option value="Travel">Travel</option>
            <option value="Shopping">Shopping</option>
            <option value="Entertainment">Entertainment</option>
            <option value="Bills">Bills</option>
            <option value="Healthcare">Healthcare</option>
            <option value="Education">Education</option>
            <option value="Housing">Housing</option>
            <option value="Transportation">Transportation</option>
            <option value="Personal Care">Personal Care</option>
            <option value="Gifts">Gifts</option>
            <option value="Other">Other</option>
          </select>
        </div>
      )}

      <div>
        <label className="block text-sm font-medium text-text-secondary mb-1">
          Description
        </label>
        <input
          type="text"
          name="description"
          value={formData.description}
          onChange={handleChange}
          placeholder="Enter description"
          className="input"
        />
      </div>

      <button
        type="submit"
        className={`btn w-full ${
          formData.type === "income" ? "btn-secondary" : "btn-primary"
        }`}
      >
        Add {formData.type.charAt(0).toUpperCase() + formData.type.slice(1)}
      </button>
    </form>
  );
};

export default TransactionForm;
