import React, { useState } from "react";
import { Tab } from "@headlessui/react";
import {
  PieChart,
  Pie,
  Cell,
  ResponsiveContainer,
  Legend,
  Tooltip,
} from "recharts";
import {
  PlusIcon,
  MinusIcon,
  ArrowTrendingUpIcon,
  ArrowTrendingDownIcon,
} from "@heroicons/react/24/outline";
import { motion, AnimatePresence } from "framer-motion";

const COLORS = ["#0088FE", "#00C49F", "#FFBB28", "#FF8042"];

function classNames(...classes) {
  return classes.filter(Boolean).join(" ");
}

const Dashboard = () => {
  const [activeTab, setActiveTab] = useState(0);
  const [transactions, setTransactions] = useState([]);
  const [newTransaction, setNewTransaction] = useState({
    type: "expense",
    amount: "",
    category: "",
    description: "",
  });

  const categories = ["Food", "Drinks", "Home", "Girlfriend", "Transportation"];

  const handleSubmit = (e) => {
    e.preventDefault();
    if (newTransaction.amount && newTransaction.category) {
      setTransactions([...transactions, { ...newTransaction, id: Date.now() }]);
      setNewTransaction({
        type: "expense",
        amount: "",
        category: "",
        description: "",
      });
    }
  };

  const getChartData = () => {
    const income = transactions
      .filter((t) => t.type === "income")
      .reduce((sum, t) => sum + Number(t.amount), 0);

    const expense = transactions
      .filter((t) => t.type === "expense")
      .reduce((sum, t) => sum + Number(t.amount), 0);

    return [
      { name: "Income", value: income },
      { name: "Expense", value: expense },
    ];
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 to-gray-800 p-6">
      <div className="max-w-7xl mx-auto">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          className="bg-gray-800 rounded-lg shadow-lg p-6 border border-gray-700"
        >
          <Tab.Group onChange={setActiveTab}>
            <Tab.List className="flex space-x-1 rounded-xl bg-gray-700 p-1">
              <Tab
                className={({ selected }) =>
                  classNames(
                    "w-full rounded-lg py-2.5 text-sm font-medium leading-5 transition-all duration-200",
                    "ring-white ring-opacity-60 ring-offset-2 ring-offset-blue-400 focus:outline-none focus:ring-2",
                    selected
                      ? "bg-blue-500 shadow text-white"
                      : "text-gray-300 hover:bg-gray-600 hover:text-white"
                  )
                }
              >
                Overview
              </Tab>
              <Tab
                className={({ selected }) =>
                  classNames(
                    "w-full rounded-lg py-2.5 text-sm font-medium leading-5 transition-all duration-200",
                    "ring-white ring-opacity-60 ring-offset-2 ring-offset-blue-400 focus:outline-none focus:ring-2",
                    selected
                      ? "bg-blue-500 shadow text-white"
                      : "text-gray-300 hover:bg-gray-600 hover:text-white"
                  )
                }
              >
                Add Transaction
              </Tab>
            </Tab.List>
            <Tab.Panels className="mt-4">
              <Tab.Panel>
                <motion.div
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ duration: 0.5 }}
                  className="grid grid-cols-1 md:grid-cols-2 gap-6"
                >
                  <div className="bg-gray-800 p-6 rounded-lg shadow hover:shadow-lg transition-shadow duration-300 border border-gray-700">
                    <h3 className="text-lg font-semibold mb-4 flex items-center text-white">
                      <ArrowTrendingUpIcon className="h-5 w-5 text-green-400 mr-2" />
                      Income vs Expense
                    </h3>
                    <div className="h-64">
                      <ResponsiveContainer width="100%" height="100%">
                        <PieChart>
                          <Pie
                            data={getChartData()}
                            cx="50%"
                            cy="50%"
                            labelLine={false}
                            outerRadius={80}
                            fill="#8884d8"
                            dataKey="value"
                            label={({ name, percent }) =>
                              `${name}: ${(percent * 100).toFixed(0)}%`
                            }
                          >
                            {getChartData().map((entry, index) => (
                              <Cell
                                key={`cell-${index}`}
                                fill={COLORS[index % COLORS.length]}
                              />
                            ))}
                          </Pie>
                          <Tooltip
                            contentStyle={{
                              backgroundColor: "#1F2937",
                              border: "1px solid #374151",
                              color: "#F3F4F6",
                            }}
                          />
                          <Legend wrapperStyle={{ color: "#F3F4F6" }} />
                        </PieChart>
                      </ResponsiveContainer>
                    </div>
                  </div>
                  <div className="bg-gray-800 p-6 rounded-lg shadow hover:shadow-lg transition-shadow duration-300 border border-gray-700">
                    <h3 className="text-lg font-semibold mb-4 flex items-center text-white">
                      <ArrowTrendingDownIcon className="h-5 w-5 text-red-400 mr-2" />
                      Recent Transactions
                    </h3>
                    <div className="space-y-4">
                      <AnimatePresence>
                        {transactions.slice(-5).map((transaction) => (
                          <motion.div
                            key={transaction.id}
                            initial={{ opacity: 0, x: -20 }}
                            animate={{ opacity: 1, x: 0 }}
                            exit={{ opacity: 0, x: 20 }}
                            className={`p-4 rounded-lg ${
                              transaction.type === "income"
                                ? "bg-green-900/30 border border-green-800"
                                : "bg-red-900/30 border border-red-800"
                            }`}
                          >
                            <div className="flex justify-between items-center">
                              <div>
                                <p className="font-medium text-white">
                                  {transaction.description}
                                </p>
                                <p className="text-sm text-gray-400">
                                  {transaction.category}
                                </p>
                              </div>
                              <div className="flex items-center">
                                {transaction.type === "income" ? (
                                  <PlusIcon className="h-5 w-5 text-green-400 mr-2" />
                                ) : (
                                  <MinusIcon className="h-5 w-5 text-red-400 mr-2" />
                                )}
                                <span
                                  className={`font-semibold ${
                                    transaction.type === "income"
                                      ? "text-green-400"
                                      : "text-red-400"
                                  }`}
                                >
                                  ₹{transaction.amount}
                                </span>
                              </div>
                            </div>
                          </motion.div>
                        ))}
                      </AnimatePresence>
                    </div>
                  </div>
                </motion.div>
              </Tab.Panel>
              <Tab.Panel>
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.5 }}
                  className="bg-gray-800 p-6 rounded-lg shadow hover:shadow-lg transition-shadow duration-300 border border-gray-700"
                >
                  <h3 className="text-lg font-semibold mb-4 text-white">
                    Add New Transaction
                  </h3>
                  <form onSubmit={handleSubmit} className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-300">
                        Type
                      </label>
                      <select
                        value={newTransaction.type}
                        onChange={(e) =>
                          setNewTransaction({
                            ...newTransaction,
                            type: e.target.value,
                          })
                        }
                        className="mt-1 block w-full rounded-md bg-gray-700 border-gray-600 text-white shadow-sm focus:border-blue-500 focus:ring-blue-500"
                      >
                        <option value="income">Income</option>
                        <option value="expense">Expense</option>
                      </select>
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-300">
                        Amount
                      </label>
                      <input
                        type="number"
                        value={newTransaction.amount}
                        onChange={(e) =>
                          setNewTransaction({
                            ...newTransaction,
                            amount: e.target.value,
                          })
                        }
                        className="mt-1 block w-full rounded-md bg-gray-700 border-gray-600 text-white shadow-sm focus:border-blue-500 focus:ring-blue-500"
                        placeholder="Enter amount"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-300">
                        Category
                      </label>
                      <select
                        value={newTransaction.category}
                        onChange={(e) =>
                          setNewTransaction({
                            ...newTransaction,
                            category: e.target.value,
                          })
                        }
                        className="mt-1 block w-full rounded-md bg-gray-700 border-gray-600 text-white shadow-sm focus:border-blue-500 focus:ring-blue-500"
                      >
                        <option value="">Select a category</option>
                        {categories.map((category) => (
                          <option key={category} value={category}>
                            {category}
                          </option>
                        ))}
                      </select>
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-300">
                        Description
                      </label>
                      <input
                        type="text"
                        value={newTransaction.description}
                        onChange={(e) =>
                          setNewTransaction({
                            ...newTransaction,
                            description: e.target.value,
                          })
                        }
                        className="mt-1 block w-full rounded-md bg-gray-700 border-gray-600 text-white shadow-sm focus:border-blue-500 focus:ring-blue-500"
                        placeholder="Enter description"
                      />
                    </div>
                    <button
                      type="submit"
                      className="w-full bg-gradient-to-r from-blue-600 to-blue-700 text-white py-2 px-4 rounded-md hover:from-blue-700 hover:to-blue-800 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition-all duration-200 transform hover:scale-[1.02]"
                    >
                      Add Transaction
                    </button>
                  </form>
                </motion.div>
              </Tab.Panel>
            </Tab.Panels>
          </Tab.Group>
        </motion.div>
      </div>
    </div>
  );
};

export default Dashboard;
