import React from "react";

const Dashboard = ({ transactions }) => {
  const totalIncome = transactions
    .filter((t) => t.type === "income")
    .reduce((sum, t) => sum + t.amount, 0);

  const totalExpenses = transactions
    .filter((t) => t.type === "expense")
    .reduce((sum, t) => sum + t.amount, 0);

  const balance = totalIncome - totalExpenses;

  const getCategoryTotal = (category) => {
    return transactions
      .filter((t) => t.type === "expense" && t.category === category)
      .reduce((sum, t) => sum + t.amount, 0);
  };

  const uniqueCategories = [
    ...new Set(
      transactions.filter((t) => t.type === "expense").map((t) => t.category)
    ),
  ];

  return (
    <div className="space-y-6">
      <h2 className="text-2xl font-bold text-gray-800 mb-6">Dashboard</h2>

      {/* Balance Card */}
      <div className="bg-gradient-to-r from-indigo-500 to-purple-500 rounded-xl p-6 text-white shadow-lg">
        <h3 className="text-lg font-semibold mb-2">Current Balance</h3>
        <p className="text-3xl font-bold">₹{balance.toLocaleString()}</p>
      </div>

      {/* Income and Expenses */}
      <div className="grid grid-cols-2 gap-4">
        <div className="bg-green-50 rounded-xl p-4 shadow-md">
          <h3 className="text-green-800 font-semibold mb-1">Income</h3>
          <p className="text-2xl font-bold text-green-600">
            ₹{totalIncome.toLocaleString()}
          </p>
        </div>
        <div className="bg-red-50 rounded-xl p-4 shadow-md">
          <h3 className="text-red-800 font-semibold mb-1">Expenses</h3>
          <p className="text-2xl font-bold text-red-600">
            ₹{totalExpenses.toLocaleString()}
          </p>
        </div>
      </div>

      {/* Category Breakdown */}
      <div className="bg-white rounded-xl p-4 shadow-md">
        <h3 className="text-gray-800 font-semibold mb-4">Expense Categories</h3>
        <div className="space-y-3">
          {uniqueCategories.map((category) => {
            const total = getCategoryTotal(category);
            const percentage = (total / totalExpenses) * 100;
            return (
              <div key={category} className="space-y-1">
                <div className="flex justify-between text-sm">
                  <span className="text-gray-600">{category}</span>
                  <span className="text-gray-800 font-medium">
                    ₹{total.toLocaleString()}
                  </span>
                </div>
                <div className="w-full bg-gray-200 rounded-full h-2">
                  <div
                    className="bg-purple-500 h-2 rounded-full"
                    style={{ width: `${percentage}%` }}
                  ></div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
