import React, { useState } from "react";

const TransactionForm = ({ onAddTransaction, categories }) => {
  const [formData, setFormData] = useState({
    amount: "",
    category: "",
    type: "expense",
    description: "",
  });

  const [error, setError] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));

    // Clear category when switching to income
    if (name === "type" && value === "income") {
      setFormData((prev) => ({
        ...prev,
        category: "",
      }));
    }
  };

  const validateForm = () => {
    if (!formData.amount || isNaN(formData.amount)) {
      setError("Please enter a valid amount");
      return false;
    }

    if (formData.type === "expense" && !formData.category) {
      setError("Please select a category for expenses");
      return false;
    }

    setError("");
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    try {
      const transaction = {
        amount: Number(formData.amount),
        type: formData.type,
        category: formData.type === "income" ? "Income" : formData.category,
        description: formData.description,
      };

      await onAddTransaction(transaction);

      // Reset form
      setFormData({
        amount: "",
        category: "",
        type: "expense",
        description: "",
      });
    } catch (error) {
      setError("Failed to add transaction. Please try again.");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      {error && (
        <div className="bg-red-50 text-red-600 p-3 rounded-lg text-sm">
          {error}
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {/* Amount Input */}
        <div className="space-y-2">
          <label className="block text-sm font-medium text-gray-700">
            Amount (₹)
          </label>
          <input
            type="number"
            name="amount"
            value={formData.amount}
            onChange={handleChange}
            placeholder={`Enter ${formData.type} amount`}
            className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
            required
          />
        </div>

        {/* Transaction Type */}
        <div className="space-y-2">
          <label className="block text-sm font-medium text-gray-700">
            Type
          </label>
          <div className="flex space-x-4">
            <label className="flex items-center space-x-2">
              <input
                type="radio"
                name="type"
                value="expense"
                checked={formData.type === "expense"}
                onChange={handleChange}
                className="text-indigo-600 focus:ring-indigo-500"
              />
              <span className="text-gray-700">Expense</span>
            </label>
            <label className="flex items-center space-x-2">
              <input
                type="radio"
                name="type"
                value="income"
                checked={formData.type === "income"}
                onChange={handleChange}
                className="text-indigo-600 focus:ring-indigo-500"
              />
              <span className="text-gray-700">Income</span>
            </label>
          </div>
        </div>
      </div>

      {/* Category Select (only for expenses) */}
      {formData.type === "expense" && (
        <div className="space-y-2">
          <label className="block text-sm font-medium text-gray-700">
            Category
          </label>
          <select
            name="category"
            value={formData.category}
            onChange={handleChange}
            className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
            required
          >
            <option value="">Select a category</option>
            {categories.map((category) => (
              <option key={category} value={category}>
                {category}
              </option>
            ))}
          </select>
        </div>
      )}

      {/* Description */}
      <div className="space-y-2">
        <label className="block text-sm font-medium text-gray-700">
          Description (Optional)
        </label>
        <input
          type="text"
          name="description"
          value={formData.description}
          onChange={handleChange}
          placeholder="Add a description"
          className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
        />
      </div>

      {/* Submit Button */}
      <button
        type="submit"
        className="w-full bg-gradient-to-r from-indigo-600 to-purple-600 text-white py-3 px-4 rounded-lg font-medium hover:from-indigo-700 hover:to-purple-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-all duration-200"
      >
        Add {formData.type === "income" ? "Income" : "Expense"}
      </button>
    </form>
  );
};

export default TransactionForm;
